import './App.css';

const INITIAL_STATE = [
  {id: 1, baslik:"Alisveriş yap", tamamlandi: false},
  {id: 2, baslik:"Fatura öde", tamamlandi: true}
]


function App() {

  const [liste,setListe] = useState(INITIAL_STATE);


  return (
    <div className="App">

    <h1>yapılacaklar listesi</h1>
    <div className="ekleme_formu">
      <input placeholder='listeye ekle' />
      <button> ekle </button>
    </div>

    <div className="liste">

      {liste.map(item => <div className={item.tamamlandi === true ? "yapıldı" :"" }>{item.baslik}</div>)}


      {/* <div>yapılacak</div>
      <div className="yapildi">yapıldı</div> */}
    </div>

    <button className="temizle">tamamlanananları temizle</button>

    </div>
  );
}

export default App;
